xpath.script:
$(document).ready(function(){
ymaps.ready(function(){
// if(typeof Cookies.get("city") == "undefined"){
if(typeof Cookies.get("city_name") == "undefined"){
ymaps.geolocation.get({
provider: 'yandex',
autoReverseGeocode: true
}).then(function (result) {
city_name = result.geoObjects.get(0).properties.get('name');
region_name =
result.geoObjects.get(0).properties.get('metaDataProperty').GeocoderMetaData.AddressDetails.Country.AdministrativeArea.AdministrativeAreaName;
$(".user-city").text(city_name);
$(".user-city").attr('data-city-name', city_name);
$(".user-city").attr('data-region-name', region_name);
$(function () {
$(".your-city:visible").popover('show');
$(".popover").css("position","fixed");
});
$(".yes-city").click(function(){
$(".your-city").popover('hide');
// Cookies.set('city', $(".user-city").text(), { expires: 365 });
Cookies.set('city_name', $(".user-city").attr('data-city-name'), { expires: 365 });
Cookies.set('region_name', $(".user-city").attr('data-region-name'), { expires: 365 });
});
$(".no-city").click(function(){
$(".your-city:visible").popover('hide')
$("#city").modal('show');
});
$(".city-selector").click(function(){
// var city = $(this).text();
// $(".user-city").text(city.split(',')[0].replace(/(\r\n|\n|\r|\s)/gm,""));
// Cookies.set('city', city, { expires: 365 });
var city_name = $(this).attr('data-city-name');
var city_id = $(this).attr('data-city-id');
var region_name = $(this).attr('data-region-name');
var region_id = $(this).attr('data-region-id');
$(".user-city").text(city_name);
Cookies.set('city_name', city_name, { expires: 365 });
Cookies.set('city_id', city_id, { expires: 365 });
Cookies.set('region_name', region_name, { expires: 365 });
Cookies.set('region_id', region_id, { expires: 365 });
$("#city").modal('hide');
});
});
} else {
$(".user-city").text(Cookies.get("city_name"));
}
});
});
